mapper.select:
select i.titlename,b.biggenre,s.smallgenre,i.turns,t.status,i.id from inventorycontrol i left join biggenre b on i.biggenre = b.biggenreid left join smallgenre s on i.smallgenre = s.smallgenreid left join status t on i.status = t.statusid where i.titlename like concat ('%',#{titleName},'%')
and b.biggenreid = #{bigGenreId}
and s.smallgenreid = #{smallGenreId}

mapper.update:
    update inventorycontrol 
    set title =#{title},biggenre =#{biggenre}, smallgenre=#{smallgenre},turns =#{turns},status =#{status}
    where  id =#{id}

    name = #{name},

    age = #{age}
where
  id = #{id}
